import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css'
import { Container, Button, Divider, Input, Segment } from 'semantic-ui-react'

import ApiClient from './ApiClient';
import { API_HOST } from './constants';


class App extends Component {
  constructor() {
    super();

    this.appId = null;
    this.APIClient = new ApiClient(API_HOST);

    this.state = {
        permissions: []
    };
  }






  render() {
    return (
      <Container>
        <Segment basic textAlign='center'>
          <Input type='text' placeholder='Search...' action>
            <input />
            <Button type='submit' onClick={this._handleGetPermissions}>Search</Button>
          </Input>
        </Segment>
      </Container>
    );
  }

  _handleGetPermissions = (e) => {
    this.appId = 'org.telegram.messenger';
    console.log(this.appId);

    this.recommendationsAPIClient.get(
        '/permissions',
        'user_id=' + this.userID + '&limit=100'
    )
        .then((response) => {
            this.setState({
                recommendedMovies: response.data.result
            });
        });

  };

}

export default App;
