import React, { Component } from 'react';
import { Container, Button, Form, Input, Segment } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css'

import ApiClient from './ApiClient';
import { API_HOST } from './constants';

const queryString = require('query-string');


class App extends Component {
  constructor() {
    super();

    this.appId = null;
    this.APIClient = new ApiClient(API_HOST);

    this.state = {
        permissions: [],
        url: ''
    };
  }






  render() {
    const { permissions, url } = this.state

    return (
      <Container>
        <Form onSubmit={this.handleGetPermissions}>
          <Form.Input
              placeholder='url...'
              name='url'
              value={url}
              onChange={this.handleChange}
            />
            <Form.Button content='Search' />
        </Form>

      </Container>
    );
  }

  handleChange = (e, { name, value }) => this.setState({ [name]: value })

  handleGetPermissions = (e) => {
    let parseUrl = queryString.parseUrl(this.state.url)

    console.log(parseUrl)

    if (!('id' in parseUrl.query)) {
      return '';
    }

    this.appId = 'org.telegram.messenger';

    this.APIClient.get(
        '/permissions',
        'appId=' + this.appId + '&hl=en'
    )
        .then((response) => {
            this.setState({
                permissions: response.data.permissions
            });
        });
  };

}

export default App;
