import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css'
import { Container, Button, Divider, Input, Segment } from 'semantic-ui-react'

import ApiClient from './ApiClient';
import { API_HOST } from './constants';


class App extends Component {
  constructor() {
    super();
    this.APIClient = new ApiClient(API_HOST);

    this.state = {
        permissions: []
    };
  }






  render() {
    return (
      <Container>
        <Segment basic textAlign='center'>
          <Input
            action={{ color: 'blue', content: 'Search' }}
            icon='search'
            iconPosition='left'
            placeholder='url'
            onClick={this._handleGetPermissions}
          />
        </Segment>
      </Container>
    );
  }

  _handleGetPermissions = (e) => {
        this.recommendationsAPIClient.get(
            '/permissions',
            'appId=' + this.appId + '&hl=en'
        )
            .then((response) => {
                this.setState({
                    recommendedMovies: response.data.result
                });
            });
    };

}

export default App;
