import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css'
import { Container, Button, Form, Input, Segment } from 'semantic-ui-react'

import ApiClient from './ApiClient';
import { API_HOST } from './constants';


class App extends Component {
  constructor() {
    super();

    this.appId = null;
    this.APIClient = new ApiClient(API_HOST);

    this.state = {
        permissions: [],
        url: ''
    };
  }






  render() {
    return (
      <Container>
        <Form onSubmit={this.handleGetPermissions}>
          <Form.Input
              placeholder='url...'
              name='url'
              value={url}
            />
            <Form.Button content='Search' />
        </Form>

      </Container>
    );
  }

  handleGetPermissions = (e) => {
    console.log(e.target.value)

    this.appId = 'org.telegram.messenger';

    this.APIClient.get(
        '/permissions',
        'appId=' + this.appId + '&hl=en'
    )
        .then((response) => {
            this.setState({
                permissions: response.data.permissions
            });
        });
  };

}

export default App;
